SELECT mc.sid AS sid, mi.store_code AS storeCode, mi.member_code AS memberCode, mi.openid AS openid, mi.password AS password, mi.mobile AS mobile, mc.card_code AS cardCode, mc.card_type AS cardType, mc.card_level AS cardLevel, mc.status AS status, mc.del_flag AS delFlag, mi.code_id as cardId FROM memberinfo mi INNER JOIN member_card mc ON mi.store_code = mc.store_code AND mi.member_code = mc.member_code
AND mc.status = #{status}
AND mc.status = 0
AND mc.del_flag = #{delFlag}
AND mc.del_flag = 0
AND mc.card_type = #{cardType}
AND mi.store_code = #{storeCode}
AND mi.openid = #{openid}
AND mi.member_code = #{memberCode}
AND mi.mobile = #{mobile}

SELECT mi.store_code AS storeCode, mi.member_code AS memberCode, mi.openid AS openid, mi.unionid AS unionid, mi.groupid AS groupid, mi.nickname AS nickname, mi.headimgurl AS headimgurl, mi.sex AS sex, mi.city AS city, mi.country AS country, mi.province AS province, mi.remark AS remark, mi.password AS password, mi.mobile AS mobile, mi.id_card AS idCard, mi.email AS email, si.business_name AS businessName, si.branch_name AS branchName FROM memberinfo mi INNER JOIN storeinfo si ON mi.store_code = si.store_code
AND mi.store_code = #{storeCode}
AND mi.openid = #{openid}
AND mi.member_code = #{memberCode}
AND mi.mobile = #{mobile}

SELECT mi.store_code AS storeCode, mi.member_code AS memberCode, mi.openid AS openid, mi.unionid AS unionid, mi.groupid AS groupid, mi.nickname AS nickname, mi.headimgurl AS headimgurl, mi.sex AS sex, mi.city AS city, mi.country AS country, mi.province AS province, mi.remark AS remark, mi.password AS password, mi.mobile AS mobile, mi.id_card AS idCard, mi.email AS email, mc.card_code AS cardCode, mc.card_type AS cardType, mc.card_level AS cardLevel, mc.status AS status, mc.del_flag AS delFlag, mi.code_id as cardId FROM memberinfo mi LEFT JOIN member_card mc ON mi.store_code = mc.store_code AND mi.member_code = mc.member_code
AND mc.status = #{status}
AND mc.del_flag = #{delFlag}
AND mc.card_type = #{cardType}
AND mi.store_code = #{storeCode}
AND mi.openid = #{openid}
AND mi.member_code = #{memberCode}
AND mi.mobile = #{mobile}

